import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { BookService } from '../services/book.service';
import { success, fail } from 'src/utils/general';
import { Book } from '../interfaces/entities';

@Component({
  selector: 'app-edit-book',
  templateUrl: './edit-book.component.html'
})
export class EditBookComponent {
  bookId: number = 0;
  name: String = '';
  author: String = '';
  description: String = '';
  year: String = '';
  price: number = 0;
  language: String = '';
  stock: number = 0;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private bookService: BookService
  ) {}

  ngOnInit() {
    this.route.params.subscribe(s => {
      this.bookId = s['bookId'];
    })
    this.loadBook();
  }

  async loadBook() {
    const book: Book = await this.bookService.getBookDetails(this.bookId);
    this.bookId = book.id;
    this.name = book.name;
    this.author = book.author;
    this.description = book.description;
    this.year = book.year;
    this.price = book.price;
    this.language = book.language;
    this.stock = book.stock;
  }

  async updateBook() {
    try
    {
      await this.bookService.updateBook(this.bookId, this.name, this.author, this.stock, this.description, this.price, this.year, this.language);
      success('Book updated successfully');
      this.router.navigate(['manager/home'])
    }
    catch(e)
    {
      fail('Error creating the book');
      console.log('error adding book');
    }
  }
}
<section class="section">
  <div class="container">
      <div class="level">
        <div class="level-left">
          <h1 class="title">Edit Book</h1>
        </div>
        <div class="level-right">
          <button class="button is-link" [routerLink]="['/manager/home']">Back</button>
        </div>
      </div>
      <form id="updateBookForm">
          <!-- Book Name Field -->
          <div class="field">
              <label class="label">Book Name</label>
              <div class="control">
                  <input class="input" type="text" placeholder="Enter the book name" [(ngModel)]="name" [ngModelOptions]="{standalone: true}" required>
              </div>
          </div>

          <!-- Author Field -->
          <div class="field">
              <label class="label">Author</label>
              <div class="control">
                  <input class="input" type="text" placeholder="Enter the author" [(ngModel)]="author" [ngModelOptions]="{standalone: true}" required>
              </div>
          </div>

          <!-- Description Field -->
          <div class="field">
              <label class="label">Description</label>
              <div class="control">
                  <textarea class="textarea" placeholder="Enter the book description" [(ngModel)]="description" [ngModelOptions]="{standalone: true}"></textarea>
              </div>
          </div>

          <!-- Image Upload Field -->
          <div class="field">
              <label class="label">Book Image</label>
              <div class="control">
                  <div class="file is-info">
                      <label class="file-label">
                          <input class="file-input" type="file" name="bookImage">
                          <span class="file-cta">
                              <span class="file-icon">
                                  <i class="fas fa-upload"></i>
                              </span>
                              <span class="file-label">
                                  Choose a file…
                              </span>
                          </span>
                      </label>
                  </div>
              </div>
          </div>

          <!-- Quantity Field -->
          <div class="field">
              <label class="label">Quantity in Stock</label>
              <div class="control">
                  <input class="input" type="number" placeholder="Enter the quantity in stock" [(ngModel)]="stock" [ngModelOptions]="{standalone: true}" required>
              </div>
          </div>

          <div class="field">
              <label class="label">Price</label>
              <div class="control">
                  <input class="input" type="number" placeholder="Enter the quantity in stock" [(ngModel)]="price" [ngModelOptions]="{standalone: true}" required>
              </div>
          </div>

          <div class="field">
              <label class="label">Release Year</label>
              <div class="control">
                  <input class="input" type="number" placeholder="Enter the year the book was released" [(ngModel)]="year" [ngModelOptions]="{standalone: true}" required>
              </div>
          </div>

          <div class="field">
              <label class="label">Language</label>
              <select class="control" [(ngModel)]="language" [ngModelOptions]="{standalone: true}">
                  <option>en</option>
                  <option>es</option>
                  <option>fr</option>
                  <option>de</option>
                  <option>it</option>
                  <option>pt</option>
                  <option>nl</option>
                  <option>ru</option>
                  <option>zh</option>
                  <option>ja</option>
                  <option>ko</option>
                  <option>ar</option>
                  <option>hi</option>
                  <option>tr</option>
                  <option>sv</option>
                  <option>fi</option>
                  <option>no</option>
                  <option>da</option>
                  <option>pl</option>
                  <option>el</option>
              </select>
          </div>

          <!-- Submit Button -->
          <div class="field">
              <div class="control">
                  <button class="button is-primary" type="submit" (click)="updateBook()" [disabled]="3 > name.length || 2 > author.length || 1 > stock || 0.01 > price || year.toString().length != 4 || language.length != 2">Update Book</button>
              </div>
          </div>
      </form>
  </div>
</section>
